			col5.AllowDBNull = true;		
			DataColumn col6 = dataTable.Columns.Add("Description", typeof(System.String));
			col6.AllowDBNull = true;		
			DataColumn col7 = dataTable.Columns.Add("LastModifiedBy", typeof(System.Int32));
			col7.AllowDBNull = true;		
			DataColumn col8 = dataTable.Columns.Add("LastModifiedDate", typeof(System.DateTime));
			col8.AllowDBNull = false;		
			DataColumn col9 = dataTable.Columns.Add("Active", typeof(System.Boolean));
			col9.AllowDBNull = false;		
			
			bulkCopy.ColumnMappings.Add("DynamicContentID", "DynamicContentID");
			bulkCopy.ColumnMappings.Add("SiteID", "SiteID");
			bulkCopy.ColumnMappings.Add("LanguageID", "LanguageID");
			bulkCopy.ColumnMappings.Add("DynamicContentType", "DynamicContentType");
			bulkCopy.ColumnMappings.Add("Title", "Title");
			bulkCopy.ColumnMappings.Add("Introduction", "Introduction");
			bulkCopy.ColumnMappings.Add("Description", "Description");
			bulkCopy.ColumnMappings.Add("LastModifiedBy", "LastModifiedBy");
			bulkCopy.ColumnMappings.Add("LastModifiedDate", "LastModifiedDate");
			bulkCopy.ColumnMappings.Add("Active", "Active");
			
			foreach(JXTPortal.Entities.DynamicContent entity in entities)
			{
				if (entity.EntityState != EntityState.Added)
					continue;
					
				DataRow row = dataTable.NewRow();
				
					row["DynamicContentID"] = entity.DynamicContentId;
							
				
					row["SiteID"] = entity.SiteId;
							
				
					row["LanguageID"] = entity.LanguageId;
							
				
					row["DynamicContentType"] = entity.DynamicContentType;
							
				
					row["Title"] = entity.Title;
							
				
					row["Introduction"] = entity.Introduction;
							
				
					row["Description"] = entity.Description;
							
				
					row["LastModifiedBy"] = entity.LastModifiedBy.HasValue ? (object) entity.LastModifiedBy  : System.DBNull.Value;
							
				
					row["LastModifiedDate"] = entity.LastModifiedDate;
							
				
					row["Active"] = entity.Active;
							
				
				dataTable.Rows.Add(row);
			}		
			
			// send the data to the server		
			bulkCopy.WriteToServer(dataTable);		
			
			// update back the state
			foreach(JXTPortal.Entities.DynamicContent entity in entities)
			{
				if (entity.EntityState != EntityState.Added)
					continue;
			
				entity.AcceptChanges();
			}
		}
				
