			DataColumn col3 = dataTable.Columns.Add("JobItemTypeID", typeof(System.Int32));
			col3.AllowDBNull = false;		
			DataColumn col4 = dataTable.Columns.Add("TotalNumberOfJobs", typeof(System.Int32));
