			DataColumn col5 = dataTable.Columns.Add("TotalAmount", typeof(System.Decimal));
			col5.AllowDBNull = false;		
			DataColumn col6 = dataTable.Columns.Add("Description", typeof(System.String));
			col6.AllowDBNull = true;		
			DataColumn col7 = dataTable.Columns.Add("Quantity", typeof(System.Int32));
			col7.AllowDBNull = false;		
			
			bulkCopy.ColumnMappings.Add("InvoiceID", "InvoiceID");
			bulkCopy.ColumnMappings.Add("AdvertiserUserID", "AdvertiserUserID");
			bulkCopy.ColumnMappings.Add("OrderID", "OrderID");
			bulkCopy.ColumnMappings.Add("JobItemTypeID", "JobItemTypeID");
			bulkCopy.ColumnMappings.Add("TotalNumberOfJobs", "TotalNumberOfJobs");
			bulkCopy.ColumnMappings.Add("TotalAmount", "TotalAmount");
			bulkCopy.ColumnMappings.Add("Description", "Description");
			bulkCopy.ColumnMappings.Add("Quantity", "Quantity");
			
			foreach(JXTPortal.Entities.Invoice entity in entities)
			{
				if (entity.EntityState != EntityState.Added)
					continue;
					
				DataRow row = dataTable.NewRow();
				
					row["InvoiceID"] = entity.InvoiceId;
							
				
					row["AdvertiserUserID"] = entity.AdvertiserUserId;
							
				
					row["OrderID"] = entity.OrderId;
							
				
					row["JobItemTypeID"] = entity.JobItemTypeId;
							
				
					row["TotalNumberOfJobs"] = entity.TotalNumberOfJobs;
							
				
					row["TotalAmount"] = entity.TotalAmount;
							
				
					row["Description"] = entity.Description;
							
				
					row["Quantity"] = entity.Quantity;
							
				
				dataTable.Rows.Add(row);
			}		
			
			// send the data to the server		
			bulkCopy.WriteToServer(dataTable);		
			
			// update back the state
			foreach(JXTPortal.Entities.Invoice entity in entities)
			{
				if (entity.EntityState != EntityState.Added)
					continue;
			
				entity.AcceptChanges();
			}
		}
				
