			database.AddInParameter(commandWrapper, "@Id", DbType.Int32,  id );
			database.AddInParameter(commandWrapper, "@KnowledgeBaseCategoryId", DbType.Int32,  knowledgeBaseCategoryId );
			database.AddInParameter(commandWrapper, "@Subject", DbType.String,  subject );
