		/// <summary>
		/// Lets you efficiently bulk insert many entities to the database.
		/// </summary>
		/// <param name="transactionManager">The transaction manager.</param>
		/// <param name="entities">The entities.</param>
		/// <remarks>
		///		After inserting into the datasource, the JXTPortal.Entities.Roles object will be updated
		/// 	to refelect any changes made by the datasource. (ie: identity or computed columns)
		/// </remarks>	
		public override void BulkInsert(TransactionManager transactionManager, TList<JXTPortal.Entities.Roles> entities)
		{
			//System.Data.SqlClient.SqlBulkCopy bulkCopy = new System.Data.SqlClient.SqlBulkCopy(this._connectionString, System.Data.SqlClient.SqlBulkCopyOptions.CheckConstraints); //, null);
			
			System.Data.SqlClient.SqlBulkCopy bulkCopy = null;
	
			if (transactionManager != null && transactionManager.IsOpen)
			{			
				System.Data.SqlClient.SqlConnection cnx = transactionManager.TransactionObject.Connection as System.Data.SqlClient.SqlConnection;
				System.Data.SqlClient.SqlTransaction transaction = transactionManager.TransactionObject as System.Data.SqlClient.SqlTransaction;
				bulkCopy = new System.Data.SqlClient.SqlBulkCopy(cnx, System.Data.SqlClient.SqlBulkCopyOptions.CheckConstraints, transaction); //, null);
			}
			else
			{
				bulkCopy = new System.Data.SqlClient.SqlBulkCopy(this._connectionString, System.Data.SqlClient.SqlBulkCopyOptions.CheckConstraints); //, null);
			}
			
			bulkCopy.BulkCopyTimeout = 360;
			bulkCopy.DestinationTableName = "Roles";
			
			DataTable dataTable = new DataTable();
			DataColumn col0 = dataTable.Columns.Add("RoleID", typeof(System.Int32));
			col0.AllowDBNull = false;		
			DataColumn col1 = dataTable.Columns.Add("ProfessionID", typeof(System.Int32));
			col1.AllowDBNull = false;		
			DataColumn col2 = dataTable.Columns.Add("RoleName", typeof(System.String));
			col2.AllowDBNull = false;		
			DataColumn col3 = dataTable.Columns.Add("Valid", typeof(System.Boolean));
			col3.AllowDBNull = false;		
			DataColumn col4 = dataTable.Columns.Add("MetaTitle", typeof(System.String));
			col4.AllowDBNull = false;		
			DataColumn col5 = dataTable.Columns.Add("MetaKeywords", typeof(System.String));
			col5.AllowDBNull = false;		
			DataColumn col6 = dataTable.Columns.Add("MetaDescription", typeof(System.String));
			col6.AllowDBNull = false;		
			
			bulkCopy.ColumnMappings.Add("RoleID", "RoleID");
			bulkCopy.ColumnMappings.Add("ProfessionID", "ProfessionID");
			bulkCopy.ColumnMappings.Add("RoleName", "RoleName");
			bulkCopy.ColumnMappings.Add("Valid", "Valid");
			bulkCopy.ColumnMappings.Add("MetaTitle", "MetaTitle");
			bulkCopy.ColumnMappings.Add("MetaKeywords", "MetaKeywords");
			bulkCopy.ColumnMappings.Add("MetaDescription", "MetaDescription");
			
			foreach(JXTPortal.Entities.Roles entity in entities)
			{
				if (entity.EntityState != EntityState.Added)
					continue;
					
				DataRow row = dataTable.NewRow();
				
					row["RoleID"] = entity.RoleId;
							
				
					row["ProfessionID"] = entity.ProfessionId;
							
				
					row["RoleName"] = entity.RoleName;
							
				
					row["Valid"] = entity.Valid;
							
				
					row["MetaTitle"] = entity.MetaTitle;
							
				
					row["MetaKeywords"] = entity.MetaKeywords;
							
				
					row["MetaDescription"] = entity.MetaDescription;
							
				
				dataTable.Rows.Add(row);
			}		
			
			// send the data to the server		
			bulkCopy.WriteToServer(dataTable);		
			
			// update back the state
			foreach(JXTPortal.Entities.Roles entity in entities)
			{
				if (entity.EntityState != EntityState.Added)
					continue;
			
				entity.AcceptChanges();
			}
		}
				
