data.value:
$(document).ready(function(){

        $('.dropdown-toggle').dropdown();

		// Form element Bootstrap class
		$('#CV-content-holder input:not([type=checkbox]):not([type=submit]):not([type=reset]):not([type=file]):not([type=image]):not([type=date]):not([type=radio]),#CV-content-holder select,#CV-content-holder textarea,#complete-profile input:not([type=checkbox]):not([type=submit]):not([type=reset]):not([type=file]):not([type=image]):not([type=date]):not([type=radio]),#complete-profile select,#complete-profile textarea').addClass('form-control');
        $('#CV-content-holder input[type=submit], #complete-profile input[type=submit]').not('#btnSaveAndContinue').addClass('btn btn-primary');
        $('#CV-content-holder input[type=reset], #complete-profile input[type=reset]').addClass('btn btn-default');

        $('input[maxlength], textarea[maxlength]').maxlength({
            alwaysShow: true,
            threshold: 20
        });

	  	$('#rootwizard').bootstrapWizard({
	  		'tabClass': 		'nav nav-pills nav-justified',
	'nextSelector':     '.wizard .pager li.next2',
            'onTabShow': function(tab, navigation, index){
                var $total = navigation.find('li').length;
                var $current = index+1;
                var $percent = ($current/$total) * 100;
                //$('#rootwizard').find('.bar').css({width:$percent+'%'});
                if($current >= $total) {
                    $('#rootwizard').find('.pager .next').hide();
                    $('#rootwizard').find('.pager .finish').show();
                    $('#rootwizard').find('.pager .finish').removeClass('disabled');
                    $('#linext').hide();
                } else {
                    $('#rootwizard').find('.pager .next').show();
                    $('#rootwizard').find('.pager .finish').hide();
                    $('#linext').show();
                }
            }
	  	});	

        if ($('#rootwizard .pager .finish').css('display') == 'inline') {
            $('#rootwizard').find('.pager .next').hide();
            $('#rootwizard').find('.pager .finish').show();
            $('#rootwizard').find('.pager .finish').removeClass('disabled');
        }

        $('input[value="option-other"]').change(function(){
        	if ($(this).is(':checked')) {
				$(this).parent().parent().next('div.othersfield').show();
        	} else {
            	$(this).parent().parent().next('div.othersfield').hide();
        	}
		});

	});
